// after site ready event
$(document).ready(function () {
  $(".single-page .post-thumbnail").css("height", $(window).height() - 60);
  // fancybox
  $(function () {
    $(".fancybox").fancybox();
  });
  $(".fancybox-media").fancybox({
    helpers: {
      media: true,
    },
    youtube: {
      autoplay: 0,
    },
  });
  // end fancybox

  $(".tabs-btns  li .name,.nav-link[data-toggle]").hover(function () {
    var tab_id = $(this).parents("li").attr("data-tabs");

    $(".tabs-btns  li").removeClass("active");
    $(".tab-content")
      .removeClass("active")
      .parents(".col-box")
      .removeClass("active");
    $("#main-box").addClass("active");
    $(this).parents("li").addClass("active");
    $('.tab-content[data-tabs="' + tab_id + '"]')
      .addClass("active")
      .parents(".col-box")
      .addClass("active");
    return false;
  });
  $(document).on("click", function (event) {
    if (
      !$(event.target).closest("#left-box").length &&
      !$(event.target).closest("#right-box").length
    ) {
      $(".tabs-btns  li").removeClass("active");
      $(".tab-content").removeClass("active");
      $(".col-box").removeClass("active");
      $("#main-box").removeClass("active");
    }
  });

  if ($(window).width() <= 800) {
    $(
      ".tabs-btns  li[data-tabs='tab-1'],.tabs-btns  li[data-tabs='tab-2']"
    ).click(function () {
      $("html, body").animate(
        {
          scrollTop: $("#right-box").offset().top,
        },
        500
      );
    });
    $(
      ".tabs-btns  li[data-tabs='tab-3'],.tabs-btns  li[data-tabs='tab-4']"
    ).click(function () {
      $("html, body").animate(
        {
          scrollTop: $("#left-box").offset().top,
        },
        500
      );
    });
  }

  $(".video-slider").slick({
    dots: true,
    infinite: true,
    speed: 700,
    slidesToShow: 2,
    // 	easing: 'bounseInOut',
    arrows: true,
    slidesToScroll: 2,
    responsive: [
      {
        breakpoint: 801,
        settings: {
          // centerMode: true,
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
      {
        breakpoint: 768,
        settings: {
          // centerMode: true,
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          // centerMode: true,
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  });

});

// after site load event
$(document).ready(function () {
  var previousScroll = 0,
    headerOrgOffset = $("#top-nav").height();

  // $('#top-nav').height($('#top-nav').height());
  $("#top-nav").addClass("active");
  $(window).scroll(function () {
    var currentScroll = $(this).scrollTop();
    if (currentScroll > headerOrgOffset) {
      if (currentScroll > previousScroll) {
        $("#top-nav").removeClass("active");
      } else {
        $("#top-nav").addClass("active");
      }
    } else {
      $("#top-nav").slideDown();
    }
    previousScroll = currentScroll;
  });
});
$(window).on("load", function () {
  setTimeout(function () {
    AOS.init({
      offset: 100,
      duration: 1200,
      easing: "ease-out-back",
      delay: 400,
      once: true,
    });
  }, 700);
});
